import React, {useState} from 'react';
import { Slide } from 'react-slideshow-image';
import './ImageSlider.css';

function ImageSlider({eventImages}) {
    const [slideImages, setSlideImages] = React.useState([]);
    const [isOpenSlider, toggleSlider] = React.useState(true);

    const properties = {
        duration: 2000,
        transitionDuration: 500,
        infinite: true,
        indicators: true,
        arrows: true,
        onChange: (oldIndex, newIndex) => {
            console.log(`slide transition from ${oldIndex} to ${newIndex}`);
        }
    };

    return (
        <div className="slide-container"
             style={`visibility: ${isOpenSlider}`}
        >
            <Slide {...properties}>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                        <span>Slide 1</span>
                    </div>
                </div>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                        <span>Slide 2</span>
                    </div>
                </div>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
                        <span>Slide 3</span>
                    </div>
                </div>
                <button onClick={() => toggleSlider(false)}>&times;</button>
            </Slide>
        </div>
    )
}

export default ImageSlider;
