import React, {useState} from 'react';
import PropTypes from 'prop-types';
import EventItem from './EventItem';

const styles = {
    ul: {
        listStyle: 'none',
        padding: 0,
        width: '80%',
        margin: 'auto'
    }
};

function EventsList(props) {
    const [eventsCount, setEventsCount] = React.useState(2);
    //setEventsCount(eventsCount);
    props.events.length = eventsCount;
    function increaseCount() {
        eventsCount++
        setEventsCount(eventsCount);

    }

    return(
        <div>
            <ul style={styles.ul}>
                {

                    props.events.map(event => {
                        return <EventItem event={event} key={event.id}/>
                    }) }
            </ul>
            <button className='show-more-btn' onClick={increaseCount}>Show more</button>
        </div>
    )
}

EventsList.propTypes = {
    events: PropTypes.arrayOf(PropTypes.object).isRequired,
};

export default EventsList;
