import React from 'react';
import PropTypes from 'prop-types';

const styles = {
    li: {
        display: 'flex',
        flexDirection: 'row',
        backgroundColor: '#F4F4F3',
        justifyContent: 'space-between',
        width: '80%',
        margin: 20,
        padding: 10
    },
    img: {
        width: 300
    },
    div: {
        backgroundColor: '#fff',
        padding: 10
    }
};

const showMoreLink = true;
function showMoreDescr() {
    console.log()
}

function EventItem({event}) {
    return(
        <li style={styles.li}>
            <div style={styles.div}>
                <h4>Ticket price</h4>
                <span>{event.price}</span>
            </div>
            <div style={styles.div} className='description-wrapper'>
                <h3>
                    {event.name}
                </h3>

                    {showMoreLink ?
                        (<p>
                            {`${event.description.substring(0, 200)}...`} <a href='#' className='read-more' onClick={showMoreDescr}>Read more</a>
                        </p>) :
                        (<p>{event.description}</p>)
                    }
            </div>
            <div>
                <img src={event.images[0]} style={styles.img} />
            </div>
        </li>
    )
}

EventItem.propTypes = {
    event: PropTypes.object.isRequired
};

export default EventItem;
