import React, {useEffect} from 'react';
import EventsList from './events/EvensList';
import AddEvent from './events/AddEvent'

function App() {
  const [events, setEvents] = React.useState([]);
  let [eventsCount, setEventsCount] = React.useState(2);

  function getEvents(eventsCount) {
      fetch('https://s3.eu-central-1.amazonaws.com/js.smartair.co.il/custom/activities.json')
          .then(resp => resp.json())
          .then((events) => {
              // Object.values() doesn't work in my version of Chrome, so I cheated in this case
              events = Object.keys(events.activities).map(key => events.activities[key]);
              events.activities.length = eventsCount;
              setEvents(events);
          })
  }

  useEffect(() => {
      getEvents(eventsCount)
  }, []);

  function addEvent() {
      setEventsCount(eventsCount++);
      getEvents(eventsCount)
  }

  return (
    <div className="wrapper">
      <h1>Upcoming events</h1>
      <EventsList events={events}/>
      <AddEvent onClick={addEvent}/>
    </div>
  );
}

export default App;
