import React, {useContext, useState} from 'react';
import { Slide } from 'react-slideshow-image';
import './ImageSlider.css';
import Context from '../context';

function ImageSlider({visibleSlider, slideImages}) {
    const {hideSlider} = useContext(Context);

    const [isOpenSlider, toggleSlider] = React.useState(visibleSlider);
    const images = slideImages[0];

    const properties = {
        duration: 2000,
        transitionDuration: 500,
        infinite: true,
        indicators: true,
        arrows: true
    };

    return (
        visibleSlider && (<div className="slide-container">
            <Slide {...properties}>
                <div className="each-slide">
                    <div style={{'background': `url(${images[0]}) no-repeat`}} />
                </div>
                <div className="each-slide">
                    <div style={{'background': `url(${images[1]}) no-repeat`}} />
                </div>
                <div className="each-slide">
                    <div style={{'background': `url(${images[2]}) no-repeat`}} />
                </div>

            </Slide>
            <button onClick={hideSlider} className='close-slider'>&times;</button>
        </div>)
    )
}

export default ImageSlider;
