import React from 'react';
import PropTypes from 'prop-types';

const styles = {
    li: {
        display: 'flex',
        flexDirection: 'row',
        backgroundColor: '#F4F4F3',
        justifyContent: 'space-between',
        width: '80%',
        margin: 20,
        padding: 10
    },
    img: {
        width: 300
    },
    div: {
        backgroundColor: '#fff',
        padding: 10
    }
};

const maxLength = 200;

function EventItem({event}) {
    return(
        <li onClick={() => console.log(event.id)} style={styles.li}>
            <div style={styles.div}>
                <h4>Ticket price</h4>
                <span>{event.price}</span>
            </div>
            <div style={styles.div} className='description-wrapper'>
                <h3>
                    {event.name}
                </h3>

                    {event.description.length > maxLength ?
                        (<p>
                            {`${event.description.substring(0, maxLength)}...`}<a href='#'>Read more</a>
                        </p>) :
                        (<p>{event.description}</p>)
                    }
            </div>
            <div>
                <img src={event.images[0]} style={styles.img} />
            </div>
        </li>
    )
}

EventItem.propTypes = {
    event: PropTypes.object.isRequired
};

export default EventItem;
