import React, {useState} from 'react';
import { Slide } from 'react-slideshow-image';
import './ImageSlider.css';

function ImageSlider({visibleSlider, slideImages}) {
    const [isOpenSlider, toggleSlider] = React.useState(visibleSlider);
    const sliderImages = slideImages;
    console.log(sliderImages);

    const properties = {
        duration: 2000,
        transitionDuration: 500,
        infinite: true,
        indicators: true,
        arrows: true
    };

    function hideSlider(){
        console.log(visibleSlider, slideImages);
        toggleSlider(false)
    }

    return (
        visibleSlider && (<div className="slide-container">
            <Slide {...properties}>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${sliderImages[0]})`}}>
                        <span>Slide 1</span>
                    </div>
                </div>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${sliderImages[1]})`}}>
                        <span>Slide 2</span>
                    </div>
                </div>
                <div className="each-slide">
                    <div style={{'backgroundImage': `url(${sliderImages[2]})`}}>
                        <span>Slide 3</span>
                    </div>
                </div>

            </Slide>
            <button onClick={hideSlider}>&times;</button>
        </div>)
    )
}

export default ImageSlider;
